import React, { Component } from "react"
import Jumbotron from "../components/Jumbotron"
import { Row, Col, Container } from "../components/Grid"
import API from "../utils/API"
import Input from "../components/Form"
import Nav from "../components/Nav"
import { BookList, BookDeleteItem } from "../components/BookList"
import Button from "../components/Button"

class Saved extends Component {
    state = {
        savedBooks: [],
        loaded: false
    }


    componentDidMount() {
        this.loadSavedBooks()
    }

    loadSavedBooks = () => {
        this.setState({ loading: true })
        API.getBook().then(res => {
            this.setState({ savedBooks: res, loaded: false })
        })
        .catch(err => console.log(err))
    }

    deleteBooks = event => {
        API.deleteBook(event.target.id)
        .then(res => console.log(res))
        .catch(err => console.log(err))
    }

    updateBooks = bookSaved => {
        const newBooks = this.state.savedBooks.filter(
            book => bookSaved._id !== book.id
        )
        this.setState({ savedBooks: newBooks, loading: false })
    }

    render() {
        return(
            <div>
            <Nav/>
                <Jumbotron/>
                <Container>
                    <Row>
                        <Col size="md-12">
                            <form>
                                <Container>
                                    <Row>
                                        <Col size="xs-9 sm-10">
                                            <Input
                                            name="bookSearch"
                                            value={this.state.bookSearch}
                                            onChange={this.handleInputChange}
                                            placeholder="Search for a book"
                                            />
                                        </Col>
                                        <Col size="xs-3 sm-2">
                                            <Button
                                            onClick={this.handleSubmitForm}
                                            type="success"
                                            className="input-lg"
                                            >
                                            Search
                                            </Button>
                                        </Col>
                                    </Row>
                                </Container>
                            </form>
                        </Col>
                    </Row>
                    <Row>
                        <Col size="xs-12">
                            {!this.state.books.length ? (
                                <h1 className="text-center">No Books to Display</h1>
                            ) : (
                                <BookList>
                                    {this.state.books.map(book => {
                                        return(
                                            <BookDeleteItem savedBooks={this.state.savedBooks} handleDeleteButton={this.handleDeleteButton}
                                            key={book.etag}
                                            title={book.volumeInfo.title}
                                            authors={book.volumeInfo.authors}
                                            image={book.volumeInfo.imageLinks.smallThumbnail}
                                            link={book.selfLink}
                                            date={book.volumeInfo.publishedDate}
                                            />
                                        )
                                    })}
                                </BookList>
                            )}
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}

export default Saved
